mapper.select:
SELECT * FROM (
SELECT TMP.*, ROWNUM ROW_ID FROM (
select a.ID,a.WERKS,a.WH_NUMBER,a.STORAGE_AREA_CODE,a.KANBAN_MSG,a.START_DATE,a.END_DATE,a.EDITOR,a.EDIT_DATE,S.VALUE KANBAN_TYPE from SYS_NEWS a
LEFT JOIN MASTERDATA_DICT S ON a.KANBAN_TYPE=S.CODE  AND S.TYPE='KANBAN_TYPE'
where 1=1

    AND a.WERKS = #{werks}


    AND a.WH_NUMBER  = #{whNumber}


    AND a.KANBAN_TYPE = #{kanbanType}

order by a.id
)TMP
WHERE ROWNUM  <=#{END})
WHERE ROW_ID > #{START}

select count(1) from sys_kanban a
where 1=1

    AND a.WERKS = #{werks}


    AND a.WH_NUMBER  = #{whNumber}


    AND a.KANBAN_TYPE = #{kanbanType}

SELECT k.* FROM SYS_NEWS k WHERE 1=1
AND k.KANBAN_TYPE = #{KANBAN_TYPE}
AND (INSTR(k.WERKS, 'C161') >=0 OR NVL(k.WERKS,'-1') = '-1') AND (INSTR(k.WH_NUMBER, 'C161') >=0 OR NVL(k.WH_NUMBER,'-1') = '-1') AND (NVL(k.START_DATE,'-1') = '-1' OR k.START_DATE < = to_char(TRUNC(sysdate),'yyyy-mm-dd') ) AND (NVL(k.END_DATE,'-1') = '-1' OR k.END_DATE >= to_char(TRUNC(sysdate),'yyyy-mm-dd') ) ORDER BY k.EDIT_DATE DESC
