mapper.select:
select p.*,
(SELECT SUM(f.order_qty) FROM ZZJ_WERKS_ORDER f WHERE f.order_no = p.order_no AND f.werks= p.werks) as production_qty
from ZZJ_PLAN p 
where 1=1 

	and p.werks=#{werks}


	and p.workshop=#{workshop}   


	and p.line=#{line}   

	and p.batch=#{zzj_plan_batch}   and p.order_no=#{order_no}

